class AppBuilder < Rails::AppBuilder
  def gemfile
    if dev = ENV["BEARD_DEV"]
      path = File.expand_path("../../..", __FILE__)
      beard = %{, :path => "#{path}"}
    end

    create_file "Gemfile", <<-G.gsub(/^ {6}/, '')
      source "http://rubygems.org"
      git "http://github.com/datamapper/dm-rails.git"
      git "http://github.com/datamapper/dm-core.git"
      git "http://github.com/datamapper/dm-active_model.git"
      git "http://github.com/datamapper/dm-sqlite-adapter.git"
      git "http://github.com/datamapper/dm-do-adapter.git"
      git "http://github.com/rspec/rspec-rails.git"
      git "git://github.com/rails/rails.git"

      gem "beard"#{beard}
      gem "dm-sqlite-adapter"
    G

    say_status :installing, "beard dependencies"
    system "bundle check 2>/dev/null 1>/dev/null"

    unless $?.exitstatus.zero?
      system "bundle install"
    else
      say_status :uptodate, "with all required dependencies"
    end
  end

  def app
    directory "app/controllers"
    directory "app/helpers"
    directory "app/models"
    create_file "app/views/layouts/application.html.haml", <<-H.gsub(/^ {6}/, '')
      !!!5
      %html
        %head
          %title= #{app_const_base}
          = stylesheet_link_tag :all
          = javascript_include_tag :defaults
          = csrf_meta_tag

        %body
          = notifications
          = yield
    H
  end

  def javascripts
    create_file "public/javascripts/rails.js",  JQUERY_JS
    create_file "public/javascripts/jquery.js", RAILS_JS
  end

  def leftovers
    run_command("rails g rspec:install")

    i = 0
    run_command("rails g devise_install") do |string|
      say_status (i.zero? ? :readme : "."), string
      i += 1
    end

    say_status :replacing, "ActiveRecord with DataMapper in initializers/devise.rb"
    gsub_file("config/initializers/devise.rb",
              %r{'devise/orm/active_record'},
              %{'devise/orm/data_mapper'},
              :verbose => false)

    run_command("rails g beard:install")
  end

  undef :test
  undef :vendor_plugins

private
  def run_command(cmd)
    IO.popen(cmd) do |io|
      while string = io.gets
        next if string =~ /^\[datamapper\]/

        if string =~ /\e\[0m/
          puts string
          next
        end

        yield string if block_given?
      end
    end
  end

  %w(say_status app_const_base gsub_file).each do |meth|
    define_method(meth) do |*args|
      @generator.send(meth, *args)
    end
  end
end

